mapper.select:
SELECT od.id as orderId, od.created_time as createTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, od.is_comment as isComment FROM orders od left JOIN order_status os ON os.order_id = od.id WHERE od.is_delete = 0
and os.order_status =#{paramsMap.orderStatus}
and od.user_id=#{paramsMap.userId} ORDER BY od.updated_time ASC

SELECT od.id as orderId, od.created_time as createTime, od.pay_method as payMethod, od.real_pay_amount as realPayAmount, od.post_amount as postAmount, os.order_status as orderStatus, oi.item_id as itemId, oi.item_name as itemName, oi.item_img as itemImg, oi.item_spec_name as iteSpecName, oi.buy_counts as buyCounts, oi.price as price FROM orders od LEFT JOIN order_items oi ON od.id=oi.order_id left JOIN order_status os ON os.order_id = od.id WHERE od.is_delete = 0
and os.order_status =#{paramsMap.orderStatus}
and od.user_id=#{paramsMap.userId} ORDER BY od.updated_time ASC
